import numpy as npimport cv2#image = cv2.imread("2\\Image_1\\IMAGE.jpg")image = cv2.imread("2\\my_image\\my_image.jpg")for i in range(1,4):  #fragment = cv2.imread(f"2\\Image_1\\Et_{i}.png")  fragment = cv2.imread(f"2\\my_image\\my{i}.jpg")  (fragment_height, fragment_width) = fragment.shape[:2]  result = cv2.matchTemplate(image, fragment, cv2.TM_CCOEFF)  (_, _, minLoc, maxLoc) = cv2.minMaxLoc(result)    topLeft = maxLoc  botRight = (topLeft[0] + fragment_width, topLeft[1] + fragment_height)  roi = image[topLeft[1]:botRight[1], topLeft[0]:botRight[0]]    mask = np.zeros(image.shape, dtype="uint8")  img_result = cv2.addWeighted(image, 0.20, mask, 0.75, 0)  img_result[topLeft[1]:botRight[1], topLeft[0]:botRight[0]] = roi  cv2.imshow("Image", img_result)  cv2.imshow("Fragment", fragment)  cv2.waitKey(0)